import { useEffect } from "react";

function User({user, onRemove, onToggle}) {
    const {id, username, email} = user;

    useEffect(() => {
        console.log(user);
    });

    return (
        <div>
            <b 
            style={{
                cursor: 'pointer',
                color: user.active ? 'green' : 'black'
            }}
            onClick={() => onToggle(id)}>
            {id}. {username}</b>: {email}
            <button onClick={() => onRemove(id)}>삭제</button>
        </div>
    );
}

export default User;
